properties.content:
[libdefaults]
  renew_lifetime = 7d
  forwardable = true
  default_realm = {{realm}}
  ticket_lifetime = 24h
  dns_lookup_realm = false
  dns_lookup_kdc = false
  #default_tgs_enctypes = {{encryption_types}}
  #default_tkt_enctypes = {{encryption_types}}

{% if domains %}
[domain_realm]
{% for domain in domains.split(',') %}
  {{domain}} = {{realm}}
{% endfor %}
{% endif %}

[logging]
  default = FILE:/var/log/krb5kdc.log
  admin_server = FILE:/var/log/kadmind.log
  kdc = FILE:/var/log/krb5kdc.log

[realms]
  {{realm}} = {
    admin_server = {{admin_server_host|default(kdc_host, True)}}
    kdc = {{kdc_host}}
  }

{# Append additional realm declarations below #}

# Set Hadoop-specific environment variables here.

#Set path to where bin/hadoop is available
#Set path to where bin/hadoop is available
export HADOOP_HOME=${HADOOP_HOME:-{{hadoop_home}}}

#set the path to where bin/hbase is available
export HBASE_HOME=${HBASE_HOME:-{{hbase_home}}}

#Set the path to where bin/hive is available
export HIVE_HOME=${HIVE_HOME:-{{hive_home}}}

#Set the path for where zookeper config dir is
export ZOOCFGDIR=${ZOOCFGDIR:-/etc/zookeeper/conf}

# add libthrift in hive to sqoop class path first so hive imports work
export SQOOP_USER_CLASSPATH="`ls ${HIVE_HOME}/lib/libthrift-*.jar 2> /dev/null`:${SQOOP_USER_CLASSPATH}"

# The java implementation to use. If JAVA_HOME is not found we expect java and jar to be in path
export JAVA_HOME={{java64_home}}

# any additional java opts you want to set. This will apply to both client and server operations
export METADATA_OPTS={{metadata_opts}}

# metadata configuration directory 
export METADATA_CONF={{conf_dir}}

# Where log files are stored. Defatult is logs directory under the base install location
export METADATA_LOG_DIR={{log_dir}}

# additional classpath entries
export METADATACPPATH={{metadata_classpath}}

# data dir
export METADATA_DATA_DIR={{data_dir}}

# pid dir
export METADATA_PID_DIR={{pid_dir}}

# Where do you want to expand the war file. By Default it is in /server/webapp dir under the base install dir.
export METADATA_EXPANDED_WEBAPP_DIR={{expanded_war_dir}}

#!/bin/bash

# Set Storm specific environment variables here.

# The java implementation to use.
export JAVA_HOME={{java64_home}}

export STORM_CONF_DIR={{conf_dir}}
export STORM_HOME={{storm_component_home_dir}}

JAVA_HOME={{java64_home}}
HADOOP_HOME=${HADOOP_HOME:-{{hadoop_home}}}

if [ -d "/usr/lib/tez" ]; then
  PIG_OPTS="$PIG_OPTS -Dmapreduce.framework.name=yarn"
fi

export JAVA_HOME={{java64_home}}
export ZOOKEEPER_HOME={{zk_home}}
export ZOO_LOG_DIR={{zk_log_dir}}
export ZOOPIDFILE={{zk_pid_file}}
export SERVER_JVMFLAGS={{zk_server_heapsize}}
export JAVA=$JAVA_HOME/bin/java
export CLASSPATH=$CLASSPATH:/usr/share/zookeeper/*

{% if security_enabled %}
export SERVER_JVMFLAGS="$SERVER_JVMFLAGS -Djava.security.auth.login.config={{zk_server_jaas_file}}"
export CLIENT_JVMFLAGS="$CLIENT_JVMFLAGS -Djava.security.auth.login.config={{zk_client_jaas_file}}"
{% endif %}

#! /usr/bin/env bash
export HADOOP_PREFIX={{hadoop_prefix}}
export HADOOP_CONF_DIR={{hadoop_conf_dir}}
export JAVA_HOME={{java64_home}}
export ZOOKEEPER_HOME={{zookeeper_home}}
export ACCUMULO_LOG_DIR={{log_dir}}
export ACCUMULO_CONF_DIR={{conf_dir}}
export ACCUMULO_TSERVER_OPTS="-Xmx{{accumulo_tserver_heapsize}}m -Xms{{accumulo_tserver_heapsize}}m"
export ACCUMULO_MASTER_OPTS="-Xmx{{accumulo_master_heapsize}}m -Xms{{accumulo_master_heapsize}}m"
export ACCUMULO_MONITOR_OPTS="-Xmx{{accumulo_monitor_heapsize}}m -Xms{{accumulo_monitor_heapsize}}m"
export ACCUMULO_GC_OPTS="-Xmx{{accumulo_gc_heapsize}}m -Xms{{accumulo_gc_heapsize}}m"
export ACCUMULO_GENERAL_OPTS="-XX:+UseConcMarkSweepGC -XX:CMSInitiatingOccupancyFraction=75 -Djava.net.preferIPv4Stack=true ${ACCUMULO_GENERAL_OPTS}"
export ACCUMULO_OTHER_OPTS="-Xmx{{accumulo_other_heapsize}}m -Xms{{accumulo_other_heapsize}}m ${ACCUMULO_OTHER_OPTS}"
# what do when the JVM runs out of heap memory
export ACCUMULO_KILL_CMD='kill -9 %p'

